import React, { Component } from 'react'; 
import { confirmAlert } from 'react-confirm-alert'; 
import 'react-confirm-alert/src/react-confirm-alert.css';

class Table extends Component {
  render() {
    return (
        <tr>
          <td>
            Nom: {this.props.obj.nom}
            <br/>          
            Prenom: {this.props.obj.prenom}
          </td>
          <td>            
          <button className="btn btn-success"
            onClick={()=>{
                confirmAlert({
                  customUI: ({ onClose }) => {
                    return (
                      <div id="bt" className='custom-ui'>
                        <div className="fermer">
                          <button id="flot" onClick={onClose}>X</button>
                        </div>
                        <div>
                           <div>
                              <form action={"http://localhost:8080/list?_method=PUT"} method="post" enctype="application/x-www-form-urlencoded">
                                <input type="hidden" name="_method" value="put" />
                                <span>Nom:</span><input type="text" name="nom"  placeholder={this.props.obj.nom} />
                                <span>Prenom:</span><input type="text" name="prenom"  placeholder={this.props.obj.prenom} />
                                <input type="hidden" name="id" value={this.props.obj.id}/>
                              <button type="submit" className="btn btn-primary">Ok</button>
                              </form>
                          </div>

                        </div>
                      </div>
                    );
                  }
                });

            }}
            > Edit</button>  
          </td>
          <td>
            <button className="btn btn-danger">Delete</button>
          </td>
        </tr>
    );
  }
}

export default Table;
