import React, { Component } from 'react';
import axios from 'axios';
import Table from '../table/Table';


import './List.css'
export default class List extends Component {

  constructor(props) {
      super(props);
      this.state = {list: []};
    }
    componentDidMount(){
      axios.get('http://localhost:8080/list')
        .then(response => {
          this.setState({ list: response.data });
        })
        .catch(function (error) {
          console.log(error);
        })
    }
    tabRow(){
      return this.state.list.map(function(object, i){
          return <Table obj={object} key={i} />;
      });
    }

    render(){
      return(
          <form action={"http://localhost:8080/list"} method="POST">
              <label> Nom:</label> <input type="text" name="nom" required="true"/>
              <label> Prenom:</label> <input type="text" name="prenom" required="true"/>
              <button>Ajouter</button>
          </form>
      )
    };
    render() {
      return (

        <div>
          <table className="table table-striped" style={{ marginTop: 20 }}>  

            <tbody>
              { this.tabRow() }
            </tbody>
          </table>
        </div>
      );
    }
  }

